
-- Création de la bdd --
CREATE DATABASE IF NOT EXISTS `station_meteo`;


-- Création des tables de la bdd --
USE 'station_meteo';

CREATE TABLE regions(
    id_region INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    nom_region VARCHAR(45) NOT NULL
);


CREATE TABLE departements(
    id_departement INT NOT NULL PRIMARY KEY,
    nom_departement VARCHAR(45) NOT NULL,
    id_region INT NOT NULL,
    FOREIGN KEY (id_region) REFERENCES regions(id_region)
);


CREATE TABLE villes(
    id_ville INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    nom_ville VARCHAR(45) NOT NULL,
    code_postal CHAR(5) DEFAULT NULL,
    id_departement INT NOT NULL,
    FOREIGN KEY (id_departement) REFERENCES departements(id_departement)
);


CREATE TABLE immeubles(
    id_immeuble INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    adresse VARCHAR(255) NOT NULL,
    id_ville INT NOT NULL,
    FOREIGN KEY (id_ville) REFERENCES villes(id_ville)
);


CREATE TABLE donnees_capteurs(
    id_donnees_capteurs INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    id_immeuble INT NOT NULL,
    date_mesure DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    temperature FLOAT NOT NULL,
    pression FLOAT NOT NULL,
    humidite FLOAT NOT NULL,
    FOREIGN KEY (id_immeuble) REFERENCES immeubles(id_immeuble)
);



